const editFormHandler=async a=>{a.preventDefault();const b=document.querySelector("#title").value.trim(),c=document.querySelector("#content").value.trim(),d=document.querySelector(".edit-form");if(id=d.dataset.post_id,b&&c){const a=await fetch(`/api/posts/${id}`,{method:"PUT",body:JSON.stringify({title:b,content:c}),headers:{"Content-Type":"application/json"}});a.ok?document.location.replace("/dashboard"):alert(a.statusText)}},deleteFormHandler=async a=>{a.preventDefault();const b=document.querySelector(".edit-form");id=b.dataset.post_id;const c=await fetch(`/api/posts/${id}`,{method:"DELETE",headers:{"Content-Type":"application/json"}});c.ok?document.location.replace("/dashboard"):alert(c.statusText)};document.querySelector(".edit-post-btn").addEventListener("click",editFormHandler),document.querySelector(".delete-post-btn").addEventListener("click",deleteFormHandler);